mapper.select:
select
  id, userName, password, loginName
from user


        and id = #{id}


        and userName = #{username}


        and password = #{password}


        and loginName = #{loginname}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from user


        and id = #{id}


        and userName = #{username}


        and password = #{password}


        and loginName = #{loginname}
